mapper.select:
select a.* , b.username , b.image , c.boardname from bbs a , users b , board c where a.usersid = b.usersid and a.boardid = c.boardid

	and a.usersid = #{usersid}


	and a.title = #{title}


	and a.boardid = #{boardid}


	and a.contents = #{contents}


	and a.addtime = #{addtime}


	and a.hits = #{hits}


	and a.repnum = #{repnum}
